actionConfiguration.body:
() => {
		let chain_bundle_asset = Register_form.data.RegisterBundle_schemaAssets.split(";");
		let length = chain_bundle_asset.length;
		let schema_asset_ids = new Array(length);		
		for (let i=0; i<length;i++){
			let schema_asset_aux = chain_bundle_asset[i].split(",");
			let asset_length = schema_asset_aux.length;
			let schema_asset_ids_aux = new Array(asset_length);
			for (let j=0; j<asset_length; j++) {
				schema_asset_ids_aux[j] = parseInt(schema_asset_aux[j]);
			}
			schema_asset_ids[i] = schema_asset_ids_aux;
		}		
		return schema_asset_ids
	}

