		var editedMarkets = data;
		var MarketArray = [];
		for (let i = 0; i < editedMarkets.length; i++) {
			var newMarket = {
				"market_id": editedMarkets[i].id,
				"market_rate_id": editedMarkets[i].market_rates[0].id,				
				"chain_id": editedMarkets[i].chain_id,
				"market_rate_chain_id": editedMarkets[i].market_rates[0].chain_id,
			};
			MarketArray.push(newMarket);
		}
		return MarketArray		
	}
}

