export default {
	getAssets: (data) => {
		var editedAssets = data;
		var assetArray = [];
		for (let i = 0; i < editedAssets.length; i++) {
			var newAsset = {
				"id": editedAssets[i].id,
				"name": editedAssets[i].asset_nft.name,
				"account_id": editedAssets[i].account_assets[0].account_id,
				"class_id": editedAssets[i].asset_classes[0].class_id,
				"chain_class_id": editedAssets[i].asset_classes[0].class.chain_id,
				"chain_id": editedAssets[i].asset_metadata.chain_id,
				"chain_meta": editedAssets[i].asset_metadata.chain_meta,				
			};
			assetArray.push(newAsset);
		}
		return assetArray		
	},
	getAccounts: (data) => {
		var editedAccounts = data;
		var accountArray = [];
		for (let i = 0; i < editedAccounts.length; i++) {
			var newAccount = {
				"label": editedAccounts[i].account_profile.name,
				"id": editedAccounts[i].id,
				"value": i,
				"chain_id": editedAccounts[i].account_profile.chain_id,
				"seed": editedAccounts[i].account_auth.seed,
			};
			accountArray.push(newAccount);
		}
		return accountArray
	},
	getClasses: (data) => {
		var editedClasses = data;
		var classArray = [];
		var assetsArray = [];
		var newClassAssets;
		for (let i = 0; i < editedClasses.length; i++) {
			for (let j=0; j < editedClasses[i].asset_classes.length; j++) {
				newClassAssets = {
					"label": editedClasses[i].asset_classes[j].asset.asset_nft.name,
					"value": j,
					"chain_id": editedClasses[i].asset_classes[j].asset.asset_metadata.chain_id,
				}
				assetsArray.push(newClassAssets);
			}
			var newClass = {
				"label": editedClasses[i].class_profile.name,
				"id": editedClasses[i].id,
				"value": i,
				"chain_id": editedClasses[i].chain_id,
				"assets": assetsArray,
			};
			classArray.push(newClass);
			assetsArray = [];
		}
		return classArray
	}
}

