export default {
	refresh_markets: () => {
		get_market.run(() => {
			market.market_dropdowns();
			market.market_all();
		}, () => showAlert('Couldnt refresh markets','warning'))
	},
	market_create_response: () => {
		market_create_sf.run(() => {
			market_rate_create_sf.run(() => {
				market_create.run(() => {market.refresh_markets(); showAlert('Market Created', 'success'); get_market.run()}, () => showAlert('Couldnt create the Market in DB', 'error'))
			}, () => showAlert('Couldnt create the Market rate in chain'))			
		}, () => showAlert('Couldnt create the Market in chain'))		
	},
	market_rate_from: () => {
		if (CreateRate_from.selectedOptionLabel != "Account") {
			return CreateRate_from.selectedOptionValue;
		} else {
			return CreateRate_rateAccountFrom.text;
		}
	},
	market_rate_to: () => {
		if (CreateRate_to.selectedOptionLabel != "Account") {
			return CreateRate_to.selectedOptionValue;
		} else {
			return CreateRate_rateAccountTo.text;
		}
	},
	submit_rates: () => {
		let array = [];
		if (Rates_table.tableData != undefined){
			array = Rates_table.tableData;
		}
		let array_metadata = [];
		if (Rates_table_metadata.tableData != undefined){
			array_metadata = Rates_table_metadata.tableData;
		}
		let new_rate = {
			"class_id": parseInt(CreateRate_classID.text),
			"asset_id": parseInt(CreateRate_assetID.text),
			"action": CreateRate_rateAction.selectedOptionValue,
			"amount": parseInt(CreateRate_amount.text),
			"from": market.market_rate_from(),
			"to": market.market_rate_to()
		}		
		let metadata = JSON.parse(CreateRate_metadata.text);		
		array.push(new_rate);
		array_metadata.push(metadata);
		storeValue("rates_table", undefined);
		storeValue("rates_table", array);
		storeValue("rates_table_metadata", undefined);
		storeValue("rates_table_metadata", array_metadata);
	},
	market_all: () => {
		storeValue("market_table", undefined);
		storeValue("market_table", get_data.getMarkets(get_market.data.sf_market));
		return appsmith.store.market_table
	},
	market_by_id: () => {
		storeValue("market_table", undefined);
		storeValue("market_table", get_data.getMarkets(get_market_by_id.data.sf_market));
		return appsmith.store.market_table
	},
	market_by_rate_id: () => {
		storeValue("market_table", undefined);
		storeValue("market_table", get_data.getMarkets(get_market_by_rate_id.data.sf_market));
		return appsmith.store.market_table
	},
	market_by_chain_ids: () => {
		storeValue("market_table", undefined);
		storeValue("market_table", get_data.getMarkets(get_market_by_chain_ids.data.sf_market));
		return appsmith.store.market_table
	},
	market_dropdowns: () => {
		storeValue("markets", undefined);
		storeValue("markets", get_data.getMarkets(get_market.data.sf_market));
		return appsmith.store.markets;
	}
}

