export default {
	refresh_escrows: () => {
		get_escrow.run(() => {
			escrows.escrow_dropdowns();
			escrows.escrow_all();
		}, () => showAlert('Couldnt refresh escrows','warning'))
	},
	escrow_register_response: () => {
		escrow_register_sf.run(() => {
			escrow_register.run(() => {escrows.refresh_escrows(); showAlert('Escrow Registered', 'success')}, () => showAlert('Couldnt register the Escrow in DB', 'error'))
		}, () => showAlert('Couldnt register the Escrow in chain'))		
	},
	escrow_create_response: () => {
		escrow_create_sf.run(() => {
			escrow_create.run(() => {
				escrow_owners.run(() => {
					escrows.refresh_escrows();
				}, () => showAlert('Couldnt submit the owners')); showAlert('Escrow Created', 'success')}, () => showAlert('Couldnt create the Escrow in DB', 'error'))
		}, () => showAlert('Couldnt create the Escrow in chain'))		
	},
	escrow_all: async() => {
		await storeValue("escrow_table", get_escrow.data.sf_escrow);
		return appsmith.store.escrow_table
	},
	escrow_dropdowns: () => {
		storeValue("escrows", undefined);
		storeValue("escrows", get_data.getEscrows(get_escrow.data.sf_escrow));
		storeValue("escrows_classes", undefined);
		storeValue("escrows_classes", get_data.getClassEscrows(get_escrow.data.sf_escrow));
		return appsmith.store.escrows;
	},
	submit_owner: () => {
		let array = [];
		if (Owners_table.tableData != undefined){
			array = Owners_table.tableData;
		}
		let new_value = {
			"owner": CreateEscrow_owner.text,
			"share": parseInt(CreateEscrow_share.text),
			"id": appsmith.store.accounts[CreateEscrow_ownerSelection.value].id
		}
		array.push(new_value);
		storeValue("owners_table", undefined);
		storeValue("owners_table", array);
	}, 
	submit_assets: () => {
		let array = [];
		if (DepositEscrow_table.tableData != undefined){
			array = DepositEscrow_table.tableData;
		}
		let new_value = {
			"class_id": parseInt(DepositEscrow_classID.text),
			"asset_id": parseInt(DepositEscrow_assetID.text),
			"amount": parseInt(DepositEscrow_amount.text),
		}
		array.push(new_value);
		storeValue("deposit_table", undefined);
		storeValue("deposit_table", array);
	},
	escrow_deposit: () => {
		let deposit_classes = [];
		let deposit_assets = [];
		let deposit_amounts = [];
		let length = DepositEscrow_table.tableData.length;		
		for (let i=0; i < length; i++) {
			if (!deposit_classes.includes(DepositEscrow_table.tableData[i].class_id)){
				deposit_classes.push(DepositEscrow_table.tableData[i].class_id)
				let assets = [];
				let amounts = [];
				for (let j=0; j < length; j++){
					if(DepositEscrow_table.tableData[j].class_id == DepositEscrow_table.tableData[i].class_id){
						assets.push(DepositEscrow_table.tableData[j].asset_id);
						amounts.push(DepositEscrow_table.tableData[j].amount);
					}
				}
				deposit_assets.push(assets);
				deposit_amounts.push(amounts);
			} 
		}		
		return {
				"seed": DepositEscrow_seed.text,
				"escrow": appsmith.store.escrows[DepositEscrow_escrowSelection.value].chain_id,
        "class_ids": deposit_classes,
        "asset_ids": deposit_assets,
        "amounts": deposit_amounts
    };
	},
	escrow_owners: () => {
		let escrow_owners = [];
		let escrow_shares = [];
		let length = Owners_table.tableData.length;		
		for (let i=0; i < length; i++) {
				escrow_owners.push(Owners_table.tableData[i].owner);
				escrow_shares.push(Owners_table.tableData[i].share);
		}
		return {
        "seed": RegisterEscrow_form.data.RegisterEscrow_seed,
    		"class_id": RegisterEscrow_form.data.RegisterEscrow_chainClassID,
    		"owners": escrow_owners,
				"shares": escrow_shares
    }
	},
  consult : () => {
		return appsmith.store.owners_table;
	}
}

export default {
	getEscrows: (data) => {
		var editedEscrows = data;
		var escrowArray = [];
		for (let i = 0; i < editedEscrows.length; i++) {
			var newEscrow = {
				"label": editedEscrows[i].id, 
				"value": i,
				"chain_id": editedEscrows[i].chain_id
			};
			escrowArray.push(newEscrow);
		}
		return escrowArray
	},
	getClassEscrows: (data) => {
		var editedEscrows = data;
		var escrowArray = [];
		for (let i = 0; i < editedEscrows.length; i++) {
			var newEscrow = {
				"label": editedEscrows[i].class_id, 
				"value": i,
				"id": editedEscrows[i].id
			};
			escrowArray.push(newEscrow);
		}
		return escrowArray
	},
	getOwners: (data) => {
		var editedOwners = data;
		var owners = [];
		for (let i = 0; i < editedOwners.length; i++) {
			var newOwner = {
				"escrow_id": CreateEscrow_ID.text,
				"owner": editedOwners[i].id,
				"share": editedOwners[i].share
			};
			owners.push(newOwner);
		}
		return owners;		
	}
}
